section .bss
res: resq 1

section .text
    global _start

_start:
    xor r8, r8 ; Zero r8 to start the count
    mov bl, [increment_paren] ; Move in the increment paren
    mov rdx, input_string ; Move the pointer to the input in
    mov r9, input_string_len ; Move the length of the input string into r9
    xor r10, r10 ; Zero r10 for the loop counter

for_loop:
    cmp r10, r9
    je print_answer
    inc r10
    cmp byte [rdx], bl
    je increment_count
    dec r8
    inc rdx
    jmp for_loop

increment_count:
    inc r8
    inc rdx
    jmp for_loop

print_answer:
    mov rdi, res
    mov rsi, 8
    mov rdx, r8
    call int_to_ascii
    mov rsi, 8
    call print
    mov rsi, 0
    call exit

